  grid-template-columns: repeat(4, 1fr); /* Four equal-width columns */
  grid-template-rows: repeat(5, 1fr); /* Five equal-height rows */
  grid-column-gap: 10px; /* 10px gap between columns */
  grid-row-gap: 10px; /* 10px gap between rows */

  & > :nth-child(1) {
    grid-area: 1 / 1 / 2 / 5; /* First item spans from row 1 to 2, column 1 to 5 */
    width: 100vw; /* Explicit width */
    height: 200px; /* Explicit height */
  }

  & > :nth-child(2) {
    grid-area: 2 / 1 / 3 / 5; /* Second item spans from row 2 to 3, column 1 to 5 */
  }

  & > :nth-child(3) {
    grid-area: 3 / 1 / 4 / 5; /* Third item spans from row 3 to 4, column 1 to 5 */
  }

  & > :nth-child(4) {
    grid-area: 4 / 1 / 6 / 3; /* Fourth item spans from row 4 to 6, column 1 to 3 */
  }

  & > :nth-child(5) {
    grid-area: 4 / 3 / 6 / 5; /* Fifth item spans from row 4 to 6, column 3 to 5 */
  }
`;





