// src/components/Navbar.js
import React from 'react';
import styled from 'styled-components';
import { FaReact } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

// Styled-components for styling the Navbar
const Nav = styled.nav`
  background: #0b59de;
  height: 60px;
  display: flex;
  justify-content: space-between;
  padding: 0 20px;
  align-items: center;
  color: #fff;
  position: sticky;
  top: 0;
  z-index: 999;
`;

const NavLogo = styled.div`
  color: #f2f2f2;
  display: flex;
  align-items: center;
  text-decoration: none;
  font-size: 24px;
  cursor: pointer;
`;

const NavMenu = styled.div`
  display: flex;
  gap: 20px;
`;

const NavItem = styled.div`
  color: #fff;
  text-decoration: none;
  cursor: pointer;

  &.nav-item-hover {
    position: relative;
    display: inline-block;

    &::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.6); /* Background to highlight */
      clip-path: circle(0 at center); /* Initially invisible */
      transition: clip-path 0.3s ease;
      z-index: -1;
      pointer-events: none; /* Allow interaction with text below */
    }

    &:hover::before {
      clip-path: circle(50px at center); /* Clipping mask on hover */
    }
  }
`;

// Navbar component
function Navbar() {
  const navigate = useNavigate(); // Hook for programmatic navigation

  // Function to handle navigation
  const handleNavigation = (path) => {
    navigate(path); // Navigate to the specified path
  };

  return (
    <Nav>
      {/* Logo and brand navigation */}
      <NavLogo onClick={() => handleNavigation('/')}>
        <FaReact style={{ marginRight: '10px' }} />
        CODEV
      </NavLogo>
      {/* Navigation Menu */}
      <NavMenu>
        <NavItem className="nav-item-hover" onClick={() => handleNavigation('/')}>Home</NavItem>
        <NavItem className="nav-item-hover" onClick={() => handleNavigation('/surface-selection')}>Surface Selection</NavItem>
        <NavItem className="nav-item-hover" onClick={() => handleNavigation('/peptide-selection')}>Peptide Selection</NavItem>
        <NavItem className="nav-item-hover" onClick={() => handleNavigation('/glossary')}>Glossary</NavItem>
        <NavItem className="nav-item-hover" onClick={() => handleNavigation('/features')}>Features</NavItem>
        <NavItem className="nav-item-hover" onClick={() => handleNavigation('/contact')}>Contact us</NavItem>
      </NavMenu>
    </Nav>
  );
}

export default Navbar;
