// src/components/Features.js
import React from 'react';
import styled from 'styled-components';
import { FaLaptopCode, FaMobileAlt, FaCloud } from 'react-icons/fa';

const FeaturesSection = styled.section`
  padding: 60px 20px;
  text-align: center;
`;

const FeaturesTitle = styled.h2`
  font-size: 36px;
  margin-bottom: 40px;
`;

const FeaturesGrid = styled.div`
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
  justify-content: center;
`;

const FeatureCard = styled.div`
  background: #fff;
  padding: 30px;
  border-radius: 10px;
  width: 300px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const FeatureIcon = styled.div`
  font-size: 48px;
  color: #61dafb;
  margin-bottom: 20px;
`;

const FeatureTitle = styled.h3`
  font-size: 24px;
  margin-bottom: 10px;
`;

const FeatureDescription = styled.p`
  font-size: 16px;
  color: #555;
`;

function Features() {
  return (
    <FeaturesSection id="features">
      <FeaturesTitle>Our Features</FeaturesTitle>
      <FeaturesGrid>
        <FeatureCard>
          <FeatureIcon>
            <FaLaptopCode />
          </FeatureIcon>
          <FeatureTitle>Responsive Design</FeatureTitle>
          <FeatureDescription>
            Our designs are mobile-first and look great on any device.
          </FeatureDescription>
        </FeatureCard>
        <FeatureCard>
          <FeatureIcon>
            <FaMobileAlt />
          </FeatureIcon>
          <FeatureTitle>Mobile Apps</FeatureTitle>
          <FeatureDescription>
            We develop high-quality mobile applications for Android and iOS.
          </FeatureDescription>
        </FeatureCard>
        <FeatureCard>
          <FeatureIcon>
            <FaCloud />
          </FeatureIcon>
          <FeatureTitle>Cloud Services</FeatureTitle>
          <FeatureDescription>
            Reliable and scalable cloud solutions for your business.
          </FeatureDescription>
        </FeatureCard>
      </FeaturesGrid>
    </FeaturesSection>
  );
}

export default Features;
