import * as React from 'react';
import { BarChart } from '@mui/x-charts/BarChart';
import { Typography } from '@mui/material';

export default function BarLabel() {
  const data = [
    { name: 'group A', value: 4 },
    { name: 'group B', value: 3 },
    { name: 'group C', value: 5 },
  ];

  return (
    <div style={{ position: 'relative', width: 500, height: 300 }}>
      <BarChart
        xAxis={[{ scaleType: 'band', data: data.map(d => d.name) }]}
        series={[
          {
            data: data.map(d => d.value),
          },
        ]}
        width={500}
        height={300}
      />
      {data.map((item, index) => (
        <Typography
          key={index}
          style={{
            position: 'absolute',
            left: `${(index + 0.5) * (500 / data.length) - 10}px`,
            bottom: `${(item.value / Math.max(...data.map(d => d.value))) * 300 + 10}px`,
            color: '#000',
          }}
        >
          {item.value}
        </Typography>
      ))}
    </div>
  );
}
