import * as React from 'react';
import { BarChart } from '@mui/x-charts/BarChart';
import { Typography } from '@mui/material';

export default function BarLabel() {
    const seriesData = [
        { data: [0.514], color: '#1976d2' },  // W
        { data: [0.697], color: '#dc004e' },  // A
        { data: [0.786], color: '#ffa726' },  // L
        { data: [0.260], color: '#7b1fa2' },  // R
        { data: [0.560], color: '#00bcd4' },  // R
        { data: [0.262], color: '#ff5722' },  // S
        { data: [0.574], color: '#8bc34a' },  // I
        { data: [0.259], color: '#cddc39' },  // R
        { data: [0.700], color: '#9c27b0' },  // R
        { data: [0.260], color: '#ffeb3b' },  // Q
        { data: [0.527], color: '#607d8b' },  // S
        { data: [0.828], color: '#795548' }   // Y
      ];

      const labels = [
        'W', 'A', 'L', 'R', 'R', 'S', 'I', 'R', 'R', 'Q', 'S', 'Y'
      ];



  // Calculate maximum value across all series to scale bar heights
  const maxValue = Math.max(...seriesData.flatMap(series => series.data));

  return (
    <div style={{ position: 'relative', width: 500, height: 300 }}>
      <BarChart
        xAxis={[{ scaleType: 'band', data: labels }]}
        series={seriesData.map((series) => ({
          data: series.data,
          color: series.color,
        }))}
        width={500}
        height={300}
      />
      {/* {seriesData.map((series, seriesIndex) =>
        series.data.map((value, dataIndex) => (
          <Typography
            key={`${seriesIndex}-${dataIndex}`}
            style={{
              position: 'absolute',
              left: `${(dataIndex + 0.5) * (500 / labels.length) - 10 + seriesIndex * 20}px`,
              top: `${300 - (value / maxValue) * 300 - 20}px`, // Adjust this to place the label on top of the bar
              color: series.color,
              fontWeight: 'bold',
            }}
          >
            {value}
          </Typography>
        ))
      )} */}
    </div>
  );
}
