import * as React from 'react';
import { BarChart } from '@mui/x-charts/BarChart';


export default function BarLabel() {
  const seriesData = [
    { data: [0.514, 0.697, 0.786, 0.260, 0.560, 0.262, 0.574, 0.259, 0.700, 0.260, 0.527, 0.828], color: '#1976d2' },
  ];

  const labels = ['W', 'A', 'L', 'R', 'R', 'S', 'I', 'R', 'R', 'Q', 'S', 'Y'];

  // Calculate maximum value across all series to scale bar heights
  // const maxValue = Math.max(...seriesData.flatMap(series => series.data));

  return (
    <div style={{ position: 'relative', width: 500, height: 300 }}>
      <BarChart
        xAxis={[{ scaleType: 'band', data: labels }]}
        series={seriesData.map((series) => ({
          data: series.data,
          color: series.color,
        }))}
        width={500}
        height={300}
      />

    </div>
  );
}
