// src/pages/SurfaceSelection.js
import React, { useState } from 'react';
import SearchBar from '../Components/SearchBar';
import styled from 'styled-components';

// Container to center the content with shadow
const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 40vh;
`;

// Pagination controls
const PaginationControls = styled.div`
  margin-top: 20px;
`;

const Button = styled.button`
  margin: 0 5px;
`;

const itemsPerPage = 5;

const SurfaceSelection = () => {
  const [currentPage, setCurrentPage] = useState(1);

  // Example data array
  const allItems = Array.from({ length: 20 }, (_, index) => `Item ${index + 1}`);

  const totalPages = Math.ceil(allItems.length / itemsPerPage);

  // Calculate the items to display on the current page
  const currentItems = allItems.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <PageContainer>
      <h1>Surface Selection</h1>
      <SearchBar />
      <p>Select surfaces for your application and explore available options here.</p>

      <div>
        {currentItems.map(item => (
          <div key={item}>{item}</div>
        ))}
      </div>

      <PaginationControls>
        <Button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Previous
        </Button>
        <span>Page {currentPage} of {totalPages}</span>
        <Button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
          Next
        </Button>
      </PaginationControls>
    </PageContainer>
  );
}

export default SurfaceSelection;
