// src/components/LogoAndBentoBoxes.js
import React from 'react';
import styled from 'styled-components';
import BarLabel from './BarLabel';
import logoImage from '../assets/final_logo-removebg-preview.png'; // Replace with your logo image/';
const Container = styled.section`
  padding: 40px;
  text-align: center;
`;

const BentoGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(1, 1fr); /* Adjust number of columns based on design */
  grid-gap: 20px;
  justify-content: center; /* Center align the grid */

  @media (max-width: 1024px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const BentoBox = styled.div`
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-1px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
  }
`;

const LogoBox = styled(BentoBox)`
  background: url(${logoImage}) center/contain no-repeat; /* Use logo image as background */
  background-size: contain; /* Ensure logo fits well */
  filter: brightness(0) invert(1); /* Make the logo black */
  color: black; /* Hide text */
  font-size: 0; /* Hide text size */
  padding: 40px; /* Adjust padding as needed */
  box-shadow: none; /* Remove shadow for the logo box */
  border: 2px solid #000000; /* Optional border for the logo box */
`;


const BoxIcon = styled.div`
  font-size: 48px;
  margin-bottom: 10px;
`;

const BoxTitle = styled.h3`
  font-size: 24px;
  margin-bottom: 10px;
`;

const BoxDescription = styled.p`
  font-size: 16px;
  color: #555;
`;

function LogoAndBentoBoxes() {
  return (
    <Container>
      <BentoGrid>
        <LogoBox>
           {/* Replace with your logo */}
        </LogoBox>
        {/* Add more boxes as needed */}
        <BentoBox>

          <BoxTitle>Graphes</BoxTitle>
          <BoxDescription><BarLabel></BarLabel> </BoxDescription>
        </BentoBox>
        <BentoBox>

          <BoxTitle>Box 2</BoxTitle>
          <BoxDescription>Description for Box 2</BoxDescription>
        </BentoBox>
        <BentoBox>

          <BoxTitle>Box 3</BoxTitle>
          <BoxDescription>Description for Box 3</BoxDescription>
        </BentoBox>
        <BentoBox>

          <BoxTitle>Box 4</BoxTitle>
          <BoxDescription>Description for Box 4</BoxDescription>
        </BentoBox>
        {/* Add more boxes as needed */}
      </BentoGrid>
    </Container>
  );
}

export default LogoAndBentoBoxes;
