// src/pages/Features.js
import React from 'react';
import styled from 'styled-components';

// Container for centering content
const FeaturesContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 60vh;  // Adjust as needed
  text-align: center;  // Center text within the container
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 2.5em;
  margin-bottom: 20px;
  color: #333;
`;

const Description = styled.p`
  font-size: 1.2em;
  margin-bottom: 40px;
  color: #666;
`;

const FeatureList = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 20px;
  width: 100%;
  max-width: 1200px;
`;

const FeatureCard = styled.div`
  background-color: #ffffff;
  border-radius: 12px;
  padding: 20px;
  width: 300px;
  box-shadow: 0 8px 12px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 16px 24px rgba(0, 0, 0, 0.2);
  }
`;

const FeatureTitle = styled.h3`
  font-size: 1.5em;
  margin-bottom: 10px;
  color: #222;
`;

const FeatureDescription = styled.p`
  font-size: 1em;
  color: #444;
`;

function Features() {
  return (
    <FeaturesContainer>
      <Title>Features</Title>
      <Description>Explore the features of CODEV and learn how it can assist you.</Description>
      <FeatureList>
        <FeatureCard>
          <FeatureTitle>Real-time Data Sync</FeatureTitle>
          <FeatureDescription>Synchronize data across multiple devices instantly.</FeatureDescription>
        </FeatureCard>
        <FeatureCard>
          <FeatureTitle>Advanced Analytics</FeatureTitle>
          <FeatureDescription>Utilize powerful tools for in-depth analytics and reporting.</FeatureDescription>
        </FeatureCard>
        <FeatureCard>
          <FeatureTitle>Customizable UI</FeatureTitle>
          <FeatureDescription>Personalize the interface to suit your preferences.</FeatureDescription>
        </FeatureCard>
        <FeatureCard>
          <FeatureTitle>Secure Storage</FeatureTitle>
          <FeatureDescription>Keep your data safe with advanced encryption methods.</FeatureDescription>
        </FeatureCard>
        <FeatureCard>
          <FeatureTitle>Seamless Integration</FeatureTitle>
          <FeatureDescription>Connect effortlessly with other popular platforms.</FeatureDescription>
        </FeatureCard>
      </FeatureList>
    </FeaturesContainer>
  );
}

export default Features;
