// src/components/Hero.js
import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  height: auto;
  width: 96vw;
  margin: 0 auto;
  position: relative;
  top: 20px;
//   display: flex;
//   justify-content: center;
//   align-items: center;
`;

const HeroContent = styled.div`
  background: #0d2538;
  padding: 40px;
  border-radius: 10px;
  text-align: center;
  color: #fff;
`;

const HeroTitle = styled.h1`
  font-size: 48px;
  margin-bottom: 20px;
`;

const HeroSubtitle = styled.p`
  font-size: 24px;
  margin-bottom: 30px;
`;

const HeroButton = styled.a`
  padding: 10px 20px;
  background: #61dafb;
  color: #0d2538;
  text-decoration: none;
  border-radius: 5px;
  font-size: 18px;
  &:hover {
    background: #fff;
  }
`;

function Hero() {
  return (
    <HeroSection>
      <HeroContent>
        <HeroTitle>Welcome to MyWebsite</HeroTitle>
        <HeroSubtitle>Your success starts here.</HeroSubtitle>
        <HeroButton href="#contact">Get Started</HeroButton>
      </HeroContent>
    </HeroSection>
  );
}

export default Hero;
