// src/pages/Features.js
import React from 'react';
import styled from 'styled-components';

// Container for centering content
const FeaturesContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 40vh;  // Adjust as needed
  text-align: center;  // Center text within the container
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 80%;
  max-width: 800px;
`;

const FeatureItem = styled.li`
  background-color: #f4f4f4;
  border: 1px solid #ddd;
  border-radius: 8px;
  padding: 16px;
  margin: 10px 0;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #e0e0e0;
    transform: translateY(-5px);
  }
`;

function Features() {
  return (
    <FeaturesContainer>
      <h1>Features</h1>
      <p>Explore the features of CODEV and learn how it can assist you.</p>
      <FeatureList>
        <FeatureItem>Feature 1: Real-time data synchronization across multiple devices.</FeatureItem>
        <FeatureItem>Feature 2: Advanced analytics and reporting tools.</FeatureItem>
        <FeatureItem>Feature 3: Customizable user interface to fit your needs.</FeatureItem>
        <FeatureItem>Feature 4: Secure and encrypted data storage.</FeatureItem>
        <FeatureItem>Feature 5: Seamless integration with other popular platforms.</FeatureItem>
      </FeatureList>
    </FeaturesContainer>
  );
}

export default Features;
