// src/components/LogoAndBentoBoxes.js
import React from 'react';
import styled from 'styled-components';
import { FaReact } from 'react-icons/fa'; // Example icon for the logo

const Container = styled.section`
  padding: 40px;
  text-align: center;
`;

const BentoGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(1, 1fr); /* Adjust number of columns based on design */
  grid-gap: 20px;
  justify-content: center; /* Center align the grid */

  @media (max-width: 1024px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const BentoBox = styled.div`
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
  }
`;

const LogoBox = styled(BentoBox)`
  font-size: 100px; /* Adjust size as needed */
  color: #61dafb; /* Adjust color as needed */
  padding: 40px; /* More padding for the logo box */
  background: rgba(255, 255, 255, 0.2); /* Semi-transparent background for the logo box */
  box-shadow: none; /* Remove shadow for the logo box */
  border: 2px solid #000000; /* Optional border for the logo box */
`;

const BoxIcon = styled.div`
  font-size: 48px;
  margin-bottom: 10px;
`;

const BoxTitle = styled.h3`
  font-size: 24px;
  margin-bottom: 10px;
`;

const BoxDescription = styled.p`
  font-size: 16px;
  color: #555;
`;

function LogoAndBentoBoxes() {
  return (
    <Container>
      <BentoGrid>
        <LogoBox>
          <FaReact /> {/* Replace with your logo */}
        </LogoBox>
        {/* Add more boxes as needed */}
        <BentoBox>
          <BoxIcon>ðŸ“¦</BoxIcon> {/* Replace with your icon */}
          <BoxTitle>Box 1</BoxTitle>
          <BoxDescription>Description for Box 1</BoxDescription>
        </BentoBox>
        <BentoBox>
          <BoxIcon>ðŸ“¦</BoxIcon>
          <BoxTitle>Box 2</BoxTitle>
          <BoxDescription>Description for Box 2</BoxDescription>
        </BentoBox>
        <BentoBox>
          <BoxIcon>ðŸ“¦</BoxIcon>
          <BoxTitle>Box 3</BoxTitle>
          <BoxDescription>Description for Box 3</BoxDescription>
        </BentoBox>
        <BentoBox>
          <BoxIcon>ðŸ“¦</BoxIcon>
          <BoxTitle>Box 4</BoxTitle>
          <BoxDescription>Description for Box 4</BoxDescription>
        </BentoBox>
        {/* Add more boxes as needed */}
      </BentoGrid>
    </Container>
  );
}

export default LogoAndBentoBoxes;
