// src/styles/GlobalStyles.js
import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    font-family: 'arial', sans-serif;
    background-color: #f9f9f9;
    cursor: url('data:image/svg+xml;charset=UTF-8,%3Csvg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 16 16"%3E%3Ccircle cx="8" cy="8" r="4" fill="%23000" /%3E%3C/svg%3E') 8 8, auto;
  }

  *, *::before, *::after {
    box-sizing: border-box;
  }
  /* Add this to your global CSS file or styled-components global styles */


.nav-item-hover {
  position: relative;
  display: inline-block;
  cursor: url('data:image/svg+xml;charset=UTF-8,%3Csvg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"%3E%3Ccircle cx="12" cy="12" r="8" fill="%23fff" /%3E%3C/svg%3E') 12 12, auto;
}

.nav-item-hover:hover::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.6); /* Background to highlight */
  clip-path: circle(50px at center); /* Clipping mask */
  z-index: -1;
  pointer-events: none; /* Allow interaction with text below */
}

.nav-item-hover:hover {
  color: #fff; /* Text color change on hover */
}




`;

export default GlobalStyle;
